class Solution {
    public String largestPalindromic(String num) {
        int freq[] = new int[10];
        for(char ch: num.toCharArray()){
            freq[ch-'0']++;
        }
        if(freq[0] == num.length()) return "0";
        StringBuilder front = new StringBuilder();
        StringBuilder back = new StringBuilder();
        Character mid = null;
        int used = 0;
        for(int i=9; i>=0;i--){
            if(front.isEmpty() && i == 0 ){
                continue;
            }
            int count = 0;
            if(freq[i]>1){
                while(freq[i]>1){
                    front.append((char)(i+'0'));
                    back.append((char)(i+'0'));
                    count++;
                    freq[i] -= 2;
                }
            }
            if(mid == null && freq[i] == 1)
                mid = (char)(i+'0');
        }
        StringBuilder result = new StringBuilder();
        if(mid != null) result.append(front).append(mid).append(back.reverse());
        else result.append(front).append(back.reverse());
        return result.toString();
    }
}
