class Solution {
public:
    string largestPalindromic(string num) {
        bool zero = false, pair = false; 
        vector<int> v(10,0);
        deque<char> dq;
        for(auto n : num){
            v[n - '0']++;
            if(n =='0')
                zero = true;
            if(v[n - '0'] % 2 == 0 && v[n - '0'] != 0 && n != '0') 
                pair = true;
        }
        string ans = "";
        for(int i = 9 ; i >=0 ; --i)
            if(v[i] % 2 == 1){
             dq.push_back((char)('0' + i));
             v[i]--;
             break;
            }

        if(pair == false){
            while(dq.size()){
                ans.push_back(dq.front());
                dq.pop_front();
            }
            return ans == "" ? "0" : ans;
        }
        if(zero == true)
            while(v[0] > 1){
                dq.push_front('0');
                dq.push_back('0');
                v[0] -= 2;
