                    dq.push_front((char)('0' + i));
                    dq.push_back((char)('0' + i));
                    v[i] -= 2;
                }

        while(dq.size()){
            ans.push_back(dq.front());
            dq.pop_front();
        }
        return ans == "" ? "0" : ans;
    }
};
