        for(int i=9;i>=0;i--)
        {
            while(mp[i]>1) //if freq of number is more than one, we can use it twice in palindrome
            {
                //we append the number if it is non zero or if the number is zero but does not appear at index 0.
                if(i!=0 || i==0 && ans.size()!=0) 
                    ans = ans + to_string(i);
               mp[i] -= 2;
            }
            if(mp[i]==1) //we want to store the largest number for the middle position
                lar = max(lar, i);

        }
        string rev=ans;
        reverse(rev.begin(), rev.end());
        if(lar == INT_MIN) //even sized string
            return ans+rev == "" ? "0" : ans+rev;
        else//odd sized string
            return ans+to_string(lar)+rev;

