const trimPallindromicNumber = (result) => {
    // console.log('Input', result);
    let i;
    for (i=0;i<result.length;i++) {
        if (result[i] === '0') {
            continue;
        } else {
            break;
        }
    }
    if (i > 0) {
        if (i === result.length) --i;
        // console.log('Remove zeroes before', i);
        result = result.slice(i, result.length);
    }

    for (i = result.length -1;i>=0;i--) {
        if (result[i] === '0') {
            continue;
        } else {
            break;
        }
    }
    if (i < result.length -1) {
        if (i === -1) ++i;
        // console.log('Remove zeroes after', i);
        result = result.slice(0, i+1);
    }

    return result;
}
var largestPalindromic = function(num) {
    const countMap = num.split('').reduce((acc, val) => {
        if (!acc[val]) acc[val] = 0;
        acc[val]++;
        return acc;
    }, {});

    const maxOddFreq = Object.entries(countMap)
    .filter(([key, value]) => value & 1)
    .reduce((acc, val) => Math.max(acc, val[0]), -1);

    const freqMap = Object.entries(countMap)
    .filter(([key, value]) => value !== 1)
    .sort((a, b) => b[0] - a[0]);

    let result = [];
    for (let i = 0;i<freqMap.length;i++) {
        const [val, freq] = freqMap[i];
        result.push(...Array(freq>>1).fill(val));
    }
    result = [...result, ...(maxOddFreq!== -1 ? [maxOddFreq]: []), ...result.reverse()];
    result = trimPallindromicNumber(result);
    return result.join('');
};
