impl Solution {
    pub fn largest_palindromic(num: String) -> String {
        let mut digits = vec![0; 10];
        let mut odd = vec![0; 10];

        for c in num.chars() {
            digits[(c as u8 - '0' as u8) as usize] += 1;
        }

        for i in 0..10 {
            odd[i] = digits[i] % 2;
            digits[i] -= odd[i];
        }

        let mut temp: Vec<char> = vec![];
        for i in (0..10).rev() {
            while digits[i] > 0 {
                temp.push(('0' as u8 + i as u8) as char);
                digits[i] -= 2;
            }
        }
        if temp.is_empty() == false && temp[0] == '0' { temp.clear(); } 

        let mut c = ' ';
        for i in (0..10).rev() {
            if odd[i] == 0 { continue }

            c = ('0' as u8 + i as u8) as char;
            break
        }

        let mut ret = temp.clone();
        if c != ' ' { temp.push(c); }
        if temp.is_empty() == false { temp.reverse(); }

        let temp = temp.iter().collect::<String>();
        let mut ret = ret.iter().collect::<String>();
        ret += &temp;

        if ret.is_empty() { return String::from("0") }

        ret
    }
}
