class Solution {
public:
    string largestPalindromic(string num)
    {
        vector<int> m(10);
        int largestOdd=-1;

        for(int i=0; i<num.size(); i++) // store frequency of every digit
            m[num[i]-'0']++;            

        string ans="";                  // In this we will create the first half of palindrome
        for(int i=9; i>=0; i--)         
        {
            if(m[i]%2==1)                       // if it has odd frequency then check if it's greatest
                largestOdd= max(largestOdd, i); // oddFrequency integer we have

            int add= m[i]/2;            

            if(i==0 and ans.size()==0)      //As we don't want to have leading zeroes
                continue;

            char c= i+'0';

            for(int i=0; i<add; i++)        //Add the characters to ans string
                ans+=c;                            

        }

        string rev= ans;
        reverse(rev.begin(), rev.end());    // reversing the first half

        if(largestOdd!=-1)
            ans+=(largestOdd+'0');      // If we have any odd occurence digit then add it

        if(ans.length()==0) return "0"; // Handling "00" case
        ans+=rev;                       //Add the reversed first half to make the final palindrome                   
        return ans; 
    }
};

