class Solution {
public:
    string largestPalindromic(string num) {
        vector<int> count(10,0);
        vector<char> pal;
        string ans = "";

        for(int i=0; i<num.size(); i++){
            count[int(num[i]) - 48]++;
        }

        int j = 0, flag = 1;
        char temp = 'a';

        for(int i=9; i>=0; i--){
            if(count[i]!=0){
                if(count[i]%2 == 0){
                    while(count[i]--){
                        char c = '0' + i;
                        pal.push_back(c);
                        ans+="1";
                    }
                }
                else if(count[i] > 1){
                    count[i]--;
                    while(count[i]--){
                        char c = '0' + i;
                        pal.push_back(c);
                        ans+="1";
                    }
                    if(flag){
                        temp = '0' + i;
                        flag = 0;
                        ans+="1";
                    }
                }
                else{
                    if(flag){
                        temp = '0' + i;
                        flag = 0;
                        ans+="1";
                    }
                }
            }
        }
        if(temp!='a') pal.push_back(temp);

        int i = 0, k=0;
        flag = 1 ;
        j = pal.size() - 1;

        while(i<=j){
            if(flag){
                ans[i] = pal[k];
                k++;
                i++;
                flag = 0;
            }
            else{
                ans[j] = pal[k];
                j--;
                k++;
                flag = 1;
            }
        }

        for(int i=ans.size()-1; i>=0; i--){
            if(ans[i] != '0'){
