		    // if frequency >1
            if(fq[i]>1){
			    // edge case, so we don't have 0's at beginning
                if(ans.size()==0&&i==0) continue;
				// copy half of the element
                int cnt=fq[i]/2;
                int sub=cnt;
                while(cnt){
                    ans+='0'+i;
                    cnt--;
                }
				// decrement original frequency as we used elements
                fq[i]-=(2*sub);
            }
