class Solution {
public:
    string largestPalindromic(string num) {
        unordered_map<char, int>umap;
        for(int i=0;i<num.size();i++){
            umap[num[i]]++;
        }
        priority_queue<pair<char, int>>pq;
        for(auto it: umap){
            pq.push({it.first, it.second});
        }
        string str = "";
        stack<char>st;
        char temp = '/';
        while(!pq.empty()){
            pair<char,int>p = pq.top();
            pq.pop();
            if(str.empty()){
                if(p.first=='0'){
                    continue;
                }
            }
            while(1){
                if(p.second==1){
                    temp = max(temp, p.first);
                    break;
                }
                str.push_back(p.first);
                st.push(p.first);
                p.second-=2;
                if(p.second==0){
                    break;
                }
            }
        }
        if(temp!='/'){
            str.push_back(temp);
        }
        while(!st.empty()){
            str.push_back(st.top());
            st.pop();
        }
        if(str==""){
            return "0";
        }
        return str;
    }
};
