class Solution {
public:
    string largestPalindromic(string num) {
        unordered_map<char, int> m;
        string res;
        for(int i=0; i<num.size(); i++)
        {
            m[num[i]]++;
        }
        for(int i='9'; i>='0'; i--)
        {
            if(m[i]>=2 && (i!='0' || res.size()))
            {
                res+=string(m[i]/2, i);
                m[i]%=2;
            }
        }
        string r=res;
        reverse(r.begin(), r.end());
        for(int i='9'; i>='0'; i--)
        {
            if(m[i]>0)
            {
                res+=i;
                break;
            }
        }
        res+=r;

        return res;
    }
};
