// repeat function for repeating characters in the beginning of string
var repeat = function(count, c, s) {
    let result = s;

    for(let i = 0; i < count; i++) {
        result = result + c;
    }
    return result;
}

// reverse of string
var reverse = function(s) {
    let result = "";

    for(let i = 0; i < s.length; i++) {
        result = s[i] + result;
    }
    return result;
}

// here we have used remain for odd number of 
// frequency so that we can place it middle of the string.

function largestPalindromic (num) {
   let s = num.toString(), map = {}, result = "", remain = "";

   for(let c of s) {
       map[c] = (map[c] || 0) + 1;
   }

   for(let i = 9; i >= 0; i--) {
       // if result is still empty while traversing 0 so no need to add zero;
       if(i == 0 && result == "") break;


       if(map[i] & 1 && map[i] > 1) {
           result = repeat(parseInt(map[i] / 2), i, result);
           remain = Math.max(i, remain);
       } else if(map[i] % 2 == 0) {
           result = repeat((map[i] / 2), i, result);
       } else if(map[i] == 1) {
           remain = Math.max(i, remain);
       }
   } 


   // checking if there is only zeros in the string return '0';
   if(result == "" && map[0] > 0 && remain === "") return '0'

   return result + remain + reverse(result);

}
