public class Solution {
    private Dictionary<char, int> _digitsCount = new Dictionary<char, int>();
    private PriorityQueue<Digit, Digit> _queue = new PriorityQueue<Digit, Digit>();

    class Digit : IComparable<Digit>
    {
        public char Value { get; set; }

        public int Count { get; set; }

        public Digit(char value, int count)
        {
            Value = value;
            Count = count;
        }

        public int CompareTo(Digit? other)
        {
            return other.Value.CompareTo(Value);
        }
    }

    public string LargestPalindromic(string num)
    {
        Setup(num);

        char? middle = null;
        var result = new StringBuilder();
        var firstPart = new Queue<char>();
        var secondPart = new Stack<char>();

        while (_queue.Count != 0)
        {
            var digit = _queue.Dequeue();
            var count = digit.Count / 2;

            for (int i = 0; i < count; i++)
            {
                firstPart.Enqueue(digit.Value);
                secondPart.Push(digit.Value);
            }
        }

        while (firstPart.Count != 0)
        {
            result.Append(firstPart.Dequeue());
        }

        foreach (var pair in _digitsCount)
        {
            if (pair.Value <= 0)
            {
                continue;
            }

            if (middle is null)
            {
                middle = pair.Key;
                continue;
            }

            if (middle < pair.Key)
            {
                middle = pair.Key;
            }
        }

        if (middle is not null)
        {
            result.Append(middle);
        }

        while (secondPart.Count != 0)
        {
            result.Append(secondPart.Pop());
        }

        var answer = result.ToString();

        if (answer == string.Empty)
        {
            return "0";
        }

        return answer;
    }

    void Setup(string num)
    {
        foreach (var digit in num)
        {
            _digitsCount.TryGetValue(digit, out int value);
            _digitsCount[digit] = value + 1;
        }

        foreach (var pair in _digitsCount)
        {
            if (pair.Value > 1)
            {
                var isEvent = pair.Value % 2 == 0;
                var countToAdd = isEvent ? pair.Value : pair.Value - 1;

                var digit = new Digit(pair.Key, countToAdd);
                _queue.Enqueue(digit, digit);

                _digitsCount[pair.Key] = pair.Value - countToAdd;
            }
        }

        if (_queue.Count != 0 && _queue.Peek().Value == '0')
        {
            _queue.Dequeue();
        }
    }
}
