class Solution {
public:
    string largestPalindromic(string num) {
        map<char,int,greater<char>>mp;
        for(char ch:num) mp[ch]++;

        int cnt=0;
        for(auto it:mp){
            if(it.second>=2) cnt+=it.second/2;
        }

        if(cnt==0){
            // that mean no char has freq>=2 so use largest char to form string;
            return string(1,mp.begin()->first);
        }

        int len=cnt*2;
        //i can add a num in b/w to inc the value even though if its freq is odd
        if(len<num.size()) len++;

        string ans=string(len,'@');
        int lo=0,hi=len-1;
        for(auto &it:mp){
            //if no char>0 has freq>=2 then in that case return biggest char as your string
            if(lo==0 && it.first=='0'){
                return string(1,mp.begin()->first);
            }

            int cnt=it.second/2;
            for(int i=0;i<cnt;++i){
                ans[lo]=ans[hi]=it.first;
                lo++;hi--;
            }

            it.second-=cnt*2;
        }

        if(lo==hi){
            for(auto it:mp){
                if(it.second>0){
                    ans[lo]=it.first;
                    break;
                }
            }
        }
        return ans;
    }
};
