class Solution {
public:
    string largestPalindromic(string num) {
        vector<int> freq(10,0);
        for(int i=0;i<num.size();i++)//store frequency of each digit present in the original string
        {
            int n=num[i]-'0';
            freq[n]++;
        }

        string ans="";
        int maxsingledig=-1;
        for(int i=freq.size()-1;i>=0;i--)
        {

            if(!(i==0 && ans=="") && freq[i]>1)//if we have more than 1 of that number , we can add it to our number
            //first condition is to avoid leading zeros
            {
                int cap=freq[i]/2;
                string str=to_string(i);
                for(int x=0;x<cap;x++)
                {
                    ans+=str;
                }
                freq[i]-=(2*cap);
            }

            if(freq[i]>0 && i>maxsingledig) 
            {
                maxsingledig=i;
            } 
        }

        if(maxsingledig!=-1)    ans+=(to_string(maxsingledig));

        string copy=ans;
        int startingpoint=(maxsingledig!=-1)?ans.size()-2:ans.size()-1;//we we have odd number of pallindrone, we will not repeat the middle digit more than once

        //now just copt all the pallindrome repeating numbers in the correct order
        for(int i=startingpoint;i>=0;i--)
        {
            ans+=copy[i];
        }

        return ans;

    }
};
