class Solution {
public:
    string largestPalindromic(string num) {
        map<char, int, greater<int>> mp;

        for(auto &ch:num) {
            mp[ch]++;
        }

        char oddMax = NULL;
        string ans = "";
        for(auto &it:mp) {
            if(ans == "" && it.first == '0') {
                if(oddMax < '0') oddMax = '0';
                continue;
            }
            int ct = it.second / 2;
            if(ct > 0) {
                while(ct--) ans += it.first;
            }
            if((it.second & 1) && oddMax < it.first) {
                oddMax = it.first;
            }
        }
        string temp = ans;
        reverse(temp.begin(), temp.end());
        if(oddMax != NULL) ans += oddMax;
        ans += temp;
        return ans;
    }
};
