# Code
```
class Solution {
public:
   string largestPalindromic(string num) {
    unordered_map<char, int> count;

    for (char c : num)
      ++count[c];

    string firstHalf = getHalf(count);
    string mid = Mid(count);
    string ans = firstHalf + mid + rev(firstHalf);
    return ans.empty() ? "0" : ans;
  }

 private:
  string getHalf(unordered_map<char, int>& count) {
    string firstHalf;
    for (char c = '9'; c >= '0'; --c) {
      auto it = count.find(c);
      if (it == cend(count))
        continue;
      int freq = it->second;
      firstHalf += string(freq / 2, c);
    }
    const int index = firstHalf.find_first_not_of('0');
    return index == -1 ? "" : firstHalf.substr(index);
  }

  string Mid(unordered_map<char, int>& count) {
    for (char c = '9'; c >= '0'; --c) {
      const auto it = count.find(c);
      if (it == cend(count))
        continue;
      const int freq = it->second;
      if (freq & 1)
        return string(1, c);
    }
    return "";
  }

  string rev(string& s) {
    return {rbegin(s), rend(s)};
  }
};
