class Solution {
public:
    string largestPalindromic(string num) {
        vector<int>v(10,0);
        for(int i=0;i<num.length();i++)
        {
            v[num[i]-'0']++;
        }
        int len=0;
        for(int i=0;i<=9;i++)
        {
            len+=v[i];
        }
        string ans;
        ans.resize(len,'#');
        int start=0;
        int end=len-1;
        for(int i=9;i>=0;i--)
        {
            if(v[i]>1)
            {
                if(start==0 and i==0)
                {
                    continue;
                }
                if(v[i]%2==0)
                {
                    while(v[i]>0)
                    {
                        ans[start]=i+'0';
                        ans[end]=i+'0';
                        start++;
                        end--;
                        v[i]-=2;
                    }
                }
                else
                {
                    while(v[i]>1)
                    {
                        ans[start]=i+'0';
                        ans[end]=i+'0';
                        start++;
                        end--;
                        v[i]-=2;
                    }
                }
            }
            if(v[i]==1)
            {
                if(ans[len/2]=='#')
                {
                    ans[len/2]=i+'0';
                    v[i]-=1;
