class Solution {
     public String largestPalindromic(String num) {
        if (num.length() == 1) return num;
        Map<Integer, Integer> freqMap = new HashMap<>();
        for (int i = 0; i < num.length(); i++) {
            Integer val = num.charAt(i) - 48;
            freqMap.put(val, freqMap.getOrDefault(val, 0) + 1);
        }
        int requiredArr[] = new int[num.length()];
        Arrays.fill(requiredArr, -1);
        int maxOneNumber = -1;
        int index = 0;
        for (Map.Entry<Integer, Integer> entry : freqMap.entrySet()) {
            int freq = entry.getValue();
            int digit = entry.getKey();
            if (freq == 1 && digit > maxOneNumber) {
                maxOneNumber = digit;
                continue;
            }
            if (freq % 2 == 0) {
                requiredArr[index++] = digit;
            } else {
                requiredArr[index++] = digit;
                freq = freq - 1;
                freqMap.put(digit, freq);
                if (digit > maxOneNumber)
                    maxOneNumber = digit;
            }

        }
        Arrays.sort(requiredArr);
        StringBuilder firstHalf = new StringBuilder();
        StringBuilder secondHalf = new StringBuilder();
        int decidingIndex = requiredArr.length - 1;
        while (requiredArr[decidingIndex] == 0) {
            decidingIndex--;
        }
        for (int i = decidingIndex; i >= 0; i--) {
            if (requiredArr[i] == -1) break;
            int freq = freqMap.get(requiredArr[i]);
            int indexVal = 0;
            while (indexVal < freq / 2) {
                firstHalf.append(requiredArr[i]);
                secondHalf.insert(0, requiredArr[i]);
                indexVal++;
            }
        }
        String result=maxOneNumber > -1 ? firstHalf.append(maxOneNumber).append(secondHalf).toString() : firstHalf.append(secondHalf).toString();
        return result.isEmpty()?"0":result;
    }


}
