var largestPalindromic = function(num) {
    const arr = Array(10).fill(0);
    for(const n of num) arr[+n]++;
    let pref = "";
    let suff = "";
    let mid = "";
    for(let i = 9; i >= 0; i--) {
        const count = Math.floor(arr[i] / 2);
        const c = i + "";
        if(count) {
            const s = c.repeat(count);
            if(pref || c !== "0") {
                pref += s;
                suff = s + suff;
                arr[i] -= count * 2;
            }
        } 
        if(!mid && arr[i]) {
            mid = c;
        }
    }

    return pref + mid + suff;
};
