class Solution {
public:
    string largestPalindromic(string num) {
        int arr[10] = {0};
        for(char it : num) {
            arr[it-'0']++;
        }

        string res = "";

        for(int i = 9; i >= 0; i--) {
            res+= string(arr[i]/2, '0' + i);
        }

        for(int i = 9; i >= 0; i--) {
            if(arr[i] % 2 == 1) {
                res+=string(1, '0'+i);
                break;
            }
        }

        for(int i = 0; i <= 9; i++) {
            res+= string(arr[i]/2, '0' + i);
        }

        int i = 0;
        while(res[i] == '0' && i < int(res.size())) {
            i++;
        }
        if(i == res.size()) return "0";
        return res.substr(i, res.size() - 2*i);

    }
};
