# Code
```
class Solution:
    def largestPalindromic(self, num: str) -> str:
        freq = {}
        for d in num:
            d = int(d)
            if d in freq:
                freq[d] += 1
            else:
                freq[d] = 1

        max_ = float("-inf")
        couple_digit = {}
        first = []
        last = []
        for k, v in freq.items():
            if v == 1:
                max_ = max(max_, k)
            else:
                couple_digit[k] = v

        for k, v in sorted(couple_digit.items(), key=lambda x: x[0], reverse=True):
            for _ in range(v // 2):
                first.append(k)
                last.insert(0, k)
            if v % 2 != 0:
                max_ = max(max_, k)

        first = "".join(map(str, first))
        last = "".join(map(str, last))
        if first != "" and int(first) == 0:
            if max_ != float("-inf"):
                return str(max_)
            else:
                return "0"
        if max_ == float("-inf"):
            return first + last
        return first + str(max_) + last


