class Solution {
 public static String largestPalindromic(String num) {
        StringBuilder kl = new StringBuilder(num);
        if (kl.toString().equals(kl.reverse().toString())) {
            if (kl.charAt(0) != '0') return num;
            else return "0";
        }
        long[] numb = new long[10];
        for (char x : num.toCharArray()) numb['9' - x]++;

        StringBuilder str = new StringBuilder();

        for (int i = 0; i < numb.length; i++)
            for (long j = 0; j < numb[i] / 2; j++)
                str.append((char) ('9' - i));

        if (str.length() != 0 && str.charAt(0) == '0') {
            StringBuilder ss = new StringBuilder();
            for (int i = 0; i < numb.length; i++) {
                if (numb[i] != 0) {
                    ss.append((char) ('9' - i));
                    return ss.toString();
                }
            }
        }
        StringBuilder ss = new StringBuilder();
        for (int i = str.length() - 1; i >= 0; i--) ss.append(str.charAt(i));
