# Code
```
class Solution {
public:
    string largestPalindromic(string num) {
      string front = "";
      string middle = "";
      string back = "";
      unordered_map<char, int> freqMap;

      for (auto ch : num) {
        freqMap[ch]++;
      }

      for (char ch = '9'; ch >= '0'; ch--) {
        while (freqMap[ch] > 1 && (ch != '0' || !front.empty())) {
          front.push_back(ch);
          back.push_back(ch);
          freqMap[ch] -= 2;
        }

        if (freqMap[ch] >= 1 && middle.empty()) {
          middle.push_back(ch);
        }
      }

      reverse(back.begin(), back.end());

      return front + middle + back;
    }
};
