class Solution {
public:
    int maxValue(TreeNode *root)
    {
        if(root==NULL)
            return INT_MIN;
        if(root->left==NULL and root->right==NULL)
            return root->val;
        int lmax=maxValue(root->left);
        int rmax=maxValue(root->right);
        return max(max(lmax,rmax),root->val);
    }
    int amountOfTime(TreeNode* root, int start)
    {
        int n=maxValue(root);
        vector<int>graph[n+1];
        // Construct our Graph using Level Order of Tree
        queue<TreeNode *>myq;
        myq.push(root);
        vector<bool>vertex(n+1,false);
        while(myq.size()!=0)
        {
            TreeNode *front=myq.front();
            myq.pop();
            if(front!=NULL)
            {
                int u=front->val;
                if(front->left!=NULL)
                {
                 int v=front->left->val;
                 graph[u].push_back(v);
                 vertex[u]=true;
                 vertex[v]=true;
                 graph[v].push_back(u);
                 myq.push(front->left);
                }    
                if(front->right!=NULL)
                {
                 int v=front->right->val;
                 graph[u].push_back(v);
                 graph[v].push_back(u);
                 vertex[u]=true;
                 vertex[v]=true;
                 myq.push(front->right);
                }   
            }
        }

        //  Graph Construction Done
        // Now APPLY BFS SHORTEST PATH
        vector<int>distance(n+1,INT_MAX);
        queue<int>graphQueue;
        graphQueue.push(start);
        distance[start]=0;
        while(graphQueue.size()!=0)
        {
            int front=graphQueue.front();
            graphQueue.pop();
            for(auto nbrs:graph[front])
            {
                if(distance[nbrs]==INT_MAX)
                {
                    distance[nbrs]=distance[front]+1;
                    graphQueue.push(nbrs);
                }
            }
        }
        int ans=INT_MIN;
        for(int i=1;i<=n;i++)
        {
            if(vertex[i] and distance[i]>ans)
            {
                ans=max(ans,distance[i]);
            }
        }
        return ans==INT_MIN?0 :ans;

    }
};
