class Solution {
public:
    int amountOfTime(TreeNode* root, int start) {
        unordered_map<int,vector<int>> m;
        unordered_map<int,int> vis;
        queue<TreeNode*> q;
        q.push(root);
        while(!q.empty())//adding neighbours
        {
            TreeNode* t=q.front();
            q.pop();
            if(t->left)
            {
                m[t->val].push_back(t->left->val);
                m[t->left->val].push_back(t->val);
                q.push(t->left);
            }
            if(t->right)
            {
                m[t->val].push_back(t->right->val);
                m[t->right->val].push_back(t->val);
                q.push(t->right);
            }
        }
        queue<pair<int,int>> pq;
        pq.push({start,0});
        int res=0;
        vis[start]=1;
        while(!pq.empty())// traversing through all neighbours
        {
            auto p=pq.front();
            pq.pop();
            res=max(res,p.second);
            for(auto x:m[p.first])
            {
                if(vis[x]==0)
                {
                    vis[x]=1;
                    pq.push({x,p.second+1});
                }
            }
        }
        return res;
    }
};
