{
    if(root==NULL)
    {
        return false;
    }
    if(root->val==val)
    {
        v.push_back(root);
        return true;
    }
    if(serach(root->left,val,v)||serach(root->right,val,v))
    {
        v.push_back(root);
        return true;
    }
    return false;
}
void find(TreeNode*root,TreeNode*blocker,int level)
