```
class Solution {
public:

    // mp will store parent of every node

    unordered_map<TreeNode*, TreeNode*> mp;

    TreeNode* initial;

    // function for find parent for every node

    void dfs(TreeNode* root, TreeNode* parent, int start)
    {
        // base case

        if(root == NULL)
            return;

        // if start is found

        if(root -> val == start)
        {
            initial = root;
        }

        // store the parent of root into map

        mp[root] = parent;

        // call for left subtree

        dfs(root -> left, root, start);

        // call for right subtree

        dfs(root -> right, root, start);
    }

    int amountOfTime(TreeNode* root, int start) {

        // call dfs for find parent of every node

        dfs(root, NULL, start);

        // apply bfs to get time to infect binary tree

        queue<TreeNode*> q;

        // push the initial node into queue

        q.push(initial);

        int minutes = 0;

        // declare a vis set

        unordered_set<TreeNode*> vis;

        // mark initial node visited

        vis.insert(initial);

        while(!q.empty())
        {
            int size = q.size();

            for(int i = 0; i < size; i++)
            {
                auto curr = q.front();

                q.pop();

                // if adjacent node is not visited the push into queue

                if(mp[curr] != NULL && vis.count(mp[curr]) == 0)
                {
                    // push the parent node in queue

                    q.push(mp[curr]);

                    // mark visited

                    vis.insert(mp[curr]);
                }

                // push the left child into queue and mark visited

                if(curr -> left && vis.count(curr -> left) == 0)
                {
                    q.push(curr -> left);

                    vis.insert(curr -> left);
                }

                // push the right child into queue and mark visited

                if(curr -> right && vis.count(curr -> right) == 0)
                {
                    q.push(curr -> right);

                    vis.insert(curr -> right);
                }
            }

            // increment the time

            minutes++;
        }

        return minutes - 1;
    }
};
