class Solution {
public:
    TreeNode* startp;
    void helper(TreeNode* root,unordered_map<int,TreeNode*>& mp,int start)
    {
        if(!root) return;
        if(root->val==start)
        {
            startp=root;
        } 
        if(root->left)
        {
            mp[root->left->val]=root;
            helper(root->left,mp,start);
        }
        if(root->right)
        {
            mp[root->right->val]=root;
            helper(root->right,mp,start);
        }
