# Code
```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int amountOfTime(TreeNode* root, int start) {
        unordered_map<TreeNode*, TreeNode*> parent;
        unordered_set<TreeNode*> infected;
        int result = 0;
        TreeNode* target = nullptr;
        findParent(root, parent);
        findNode(root, start, target);
        dfs(target, parent, infected, 0, result);
        return result;
    }

private:
    void findParent(TreeNode* root, unordered_map<TreeNode*, TreeNode*>& parent) {
        if (!root) return;
        if (root->left) {
            parent[root->left] = root;
            findParent(root->left, parent);
        }
        if (root->right) {
            parent[root->right] = root;
            findParent(root->right, parent);
        }
    }

    void findNode(TreeNode* root, int& start, TreeNode*& target) {
        if (!root) return;
        if (root->val == start) {
            target = root;
            return;
        }
        findNode(root->left, start, target);
        findNode(root->right, start, target);
    }

    void dfs(TreeNode* root, unordered_map<TreeNode*, TreeNode*>& parent,
                unordered_set<TreeNode*>& infected, int minutes, int& result) 
    {
        if (!root) {
            result = max(result, minutes - 1);
            return;
        }
        if (infected.count(root) > 0) return;
        infected.insert(root);
        dfs(root->left, parent, infected, minutes + 1, result);
        dfs(root->right, parent, infected, minutes + 1, result);
        dfs(parent[root], parent, infected, minutes + 1, result);
    }
};
```

