 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int amountOfTime(TreeNode* root, int start) {
        // mark the parent pointera and target node;
        map<TreeNode*,TreeNode*> par;
        map<TreeNode*,bool> vis;
        TreeNode* curr= root;
        queue<TreeNode*> q;
        q.push(root);
        int time=0;
        while (!q.empty()){
            int sz=q.size();
            for (int i=0; i<sz; i++){
                TreeNode* node= q.front();
                vis[node]=0;
                if (node->val == start)curr= node;
                q.pop();
                if(node->left){
                    q.push(node->left);
                    par[node->left]= node;
                }
                if (node->right){
                    q.push(node->right);
                    par[node->right]= node;
                }
            }
        }
        // burning the tree;
        q.push(curr);
        vis[curr]=1;
        while (!q.empty()){
            int sz=q.size();
            bool burnt=0;
            for (int i=0; i<sz; i++){
                TreeNode* node= q.front();
                q.pop();
                if (par[node] && !vis[par[node]]){
                    burnt=1;
                    q.push(par[node]);
                    vis[par[node]]=1;
                }
                if (node->left && !vis[node->left]){
                    burnt=1;
                    q.push(node->left);
                    vis[node->left]=1;
                }
                if (node->right && !vis[node->right]){
                    burnt=1;
                    q.push(node->right);
                    vis[node->right]=1;
                }
            }
            if (burnt)time++;
        }
        return time;
    }
};
