var amountOfTime = function(root, start) {
    // Create an adjacency list for the tree
    const adjList =  createAdjacentList(root);

    // If the adjacency list is empty, return 0
    if(adjList.size === 0) return 0;

    // Perform a breadth-first search on the adjacency list
    const totalTime = bfs(adjList, start);

    return totalTime;
