// TC O(n) SP = O(n)
const bfs = (adjList, start) => {
    // Set to keep track of visited nodes
    const visited = new Set();
    visited.add(start);

    // Queue to keep track of nodes to visit
    const queue = new Queue();
    queue.enqueue([start, 0]);

    // Variable to keep track of the maximum time
    let maxTime = 0;
    while(!queue.isEmpty()){
        // Dequeue the next node to visit
        const [node, time] = queue.dequeue();
        maxTime = Math.max(time, maxTime);

        // Get the adjacent nodes for the current node
        const val = adjList.get(node);

        // Enqueue the adjacent nodes if they have not been visited
        for(const child of val){
            if(!visited.has(child)){
                queue.enqueue([child, time + 1]);
                visited.add(child);
            }
        } 
    }
    return maxTime;
}

