class Solution {
    TreeNode infectedNode;
    void fillParents(HashMap < TreeNode, TreeNode > map, TreeNode root, int start) {
        if (root == null) {
            return;
        }
        Queue < TreeNode > q = new ArrayDeque < > ();
        q.add(root);

        while (q.size() > 0) {
            TreeNode rem = q.remove();
            if (rem.val == start) {
                infectedNode = rem;
            }
            if (rem.left != null) {
                //child vs parent
                map.put(rem.left, rem);
                q.add(rem.left);
            }
            if (rem.right != null) {
                //child vs parent
                map.put(rem.right, rem);
                q.add(rem.right);
            }
        }
    }

    public int amountOfTime(TreeNode root, int start) {
        /* Parent -> Child*/
        HashMap < TreeNode, TreeNode > map = new HashMap < > ();
        infectedNode = null;
        fillParents(map, root, start);
        HashSet < TreeNode > vis = new HashSet < > ();
        Queue < TreeNode > q = new ArrayDeque < > ();
        q.add(infectedNode);
        // System.out.print(infectedNode.val);
        int time = 0;
        while (!q.isEmpty()) {
            int size = q.size();
            boolean infected = false;

            while (size-- > 0) {
                TreeNode rem = q.remove();
                vis.add(rem);

                if (rem.left != null && !vis.contains(rem.left)) {
                    infected = true;
                    q.add(rem.left);
                    vis.add(rem.left);
                }

                if (rem.right != null && !vis.contains(rem.right)) {
                    infected = true;
                    q.add(rem.right);
                    vis.add(rem.right);
                }

                if (map.get(rem) != null && !vis.contains(map.get(rem))) {
                    infected = true;
                    q.add(map.get(rem));
                    vis.add(map.get(rem));
                }
            }

            if (infected) {
                time++;
            }
        }
        return time;
    }
}
