        //step 1 : create node to parent mapping and find the start node.
        map<TreeNode*, TreeNode*> nodeToParent;
        TreeNode *target = createMapping(root , start , nodeToParent);

        //step 2 : burn the tree.
        int time = 0;
        solve(target , nodeToParent , time);
        return time;
    }
