        gh=vector<vector<int>>(100001);
        traverse(root);

        queue<int> q;
        q.push(start);

        vector<bool> vis(100001,false);

        int minTimes=0;

        vis[start]=true;

        while(!q.empty()){

            for(int i=q.size();i>0;i--){

                int x=q.front();
                q.pop();

                for(auto it:gh[x]){
                    if(vis[it]==false){
                        vis[it]=true;
                        q.push(it);
                    }
                }
            }

            minTimes++;
        }

        return minTimes-1;
