class Solution {
public:
    unordered_map<TreeNode*,TreeNode*> parents ;
    TreeNode* k  = nullptr ;
    int start ;

    void find_parent(TreeNode * root){
        queue<TreeNode*> q ;
        q.push(root) ;

        while(q.size()){
            auto node = q.front() ;
            q.pop() ;
            if(node->val == start) k = node ;

            if(node->left) parents[node->left] = node , q.push(node->left) ;
            if(node->right) parents[node->right] = node , q.push(node->right) ;
        }
        return ;
    }

    int amountOfTime(TreeNode* root, int start) {
        parents[root] = nullptr   ; this->start = start ;
        int timer = -1 ;
        unordered_set<int> vis ;

        find_parent(root) ;

        queue<TreeNode*> q ;
        q.push(k);


        while(q.size()){
            int sz = q.size() ;
            while(sz--){
                auto node = q.front() ; q.pop() ;
                if(vis.find(node->val) != end(vis)) continue ;
                vis.insert(node->val) ;

                if(node->left and vis.find(node->left->val) == end(vis)) q.push(node->left) ;
                if(node->right and vis.find(node->right->val) == end(vis)) q.push(node->right) ;
                if(parents[node] != nullptr and vis.find(parents[node]->val) == end(vis)) q.push(parents[node]) ;

            }
            ++timer ;
        }

        return timer ;

    }
};
