class Solution {

    int res = 0;
    public int amountOfTime(TreeNode root, int start) {

        if(root.left == null && root.right == null)
            return 0;

        if(root.val == start){
            return getHeight(root)-1;
        }

        res = 0;

        getDiameter(root, start);

        return res;
    }

    public int getDiameter(TreeNode node, int start) {

        if(node == null)
            return 0;

        if(node.val == start) {
            res = getHeight(node)-1;
            return -1;
        }

        int left = getDiameter(node.left, start);
        int right = getDiameter(node.right, start);

        if(left >=0 && right >= 0) {
            return Math.max(left, right)+1;
        }

        if(left < 0) {
            res = Math.max(right - left, res);
            return --left;
        }

        res = Math.max(left - right , res);
        return --right;
    }

    public int getHeight(TreeNode node){

        if(node == null)
            return 0;

        return Math.max(getHeight(node.left), getHeight(node.right))+1;

    }

}
