class Solution {
    HashMap<Integer,ArrayList<Integer>> map = new HashMap<>();
    //doing bfs for height from start position
    public int amountOfTime(TreeNode root, int start) {
        makeGraph(root , -1);
        Queue<Integer> q = new LinkedList<Integer>();
        q.offer(start);
        int ans = 0;
        HashSet<Integer> set = new HashSet<>();
        set.add(start);
        while(!q.isEmpty()) {
            int size = q.size();
            while(size-->0) {
                int e = q.poll();
                if(!map.containsKey(e))
                    continue;
                for(int x : map.get(e)) {
                    if(!set.contains(x)) {
                        q.offer(x);
                        set.add(x);
                    }
                }
            }
            ans++;
        }
        return ans-1;

    }
    //making a graph using hashmap
    public void makeGraph(TreeNode root , int parent) {
        if(root == null) {
            return;
        }
        if(parent != -1) {
            if(!map.containsKey(root.val)) {
                ArrayList<Integer> list = new ArrayList<>();
                list.add(parent);
                map.put(root.val,list);
            }
            else {
                ArrayList<Integer> list = map.get(root.val);
                list.add(parent);
                map.put(root.val , list) ;
            }
            if(!map.containsKey(parent)) {
                ArrayList<Integer> list = new ArrayList<>();
                list.add(root.val);
                map.put(parent,list);
            }
            else {
                ArrayList<Integer> list = map.get(parent);
                list.add(root.val);
                map.put(parent , list) ;
            }
        }
        makeGraph(root.left , root.val);
        makeGraph(root.right , root.val);
    }
}

