        int res = 0;
        while(!q.isEmpty()){
            int size = q.size();
            for(int i=0;i<size;i++){
                int cur = q.poll();
                cnt--;
                if(cnt == 0)
                    return res;
                for(int nei : map.getOrDefault(cur, new HashSet<>())){
                    if(visited.add(nei))
                        q.offer(nei);
