/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int TimeTaken(TreeNode* end,map<TreeNode*,TreeNode*>& par)
    {
        map<TreeNode*,bool> vis;
        queue<TreeNode*> q;
        q.push(end);
        vis[end]=true;
        int time=0;

        while(!q.empty())
        {
            int size = q.size();
            int flag=0;

            for(int i=0;i<size;i++)
            {
                TreeNode* node = q.front();
                q.pop();
                vis[node]=true;

                // left child
                if(node->left && !vis[node->left])
                {
                    flag=1;
                    q.push(node->left);
                    vis[node->left]=true;
                }

                // right child
                if(node->right && !vis[node->right])
                {
                    flag=1;
                    q.push(node->right);
                    vis[node->right]=true;
                }

                // parent
                if(par[node] && !vis[par[node]])
                {
                    flag=1;
                    q.push(par[node]);
                    vis[par[node]]=true;
                }
            }

            if(flag) time++;            // if we have any node ( child , parent , right ) then increase count by 1
        }
        return time;
    }

    TreeNode* Parent(TreeNode*& root,map<TreeNode*,TreeNode*>& par,int start)
    {
        TreeNode* target;
        queue<TreeNode*> q;
        q.push(root);

        while(!q.empty())
        {
            TreeNode* node = q.front();
            q.pop();

            if(node->val==start)
                target=node;                  // starting node

            // left child
            if(node->left)
            { 
                q.push(node->left);
                par[node->left] = node;
            }

            // right child
            if(node->right)
            {
                q.push(node->right);
                par[node->right] = node;
            }
        }
        return target;
    }

    int amountOfTime(TreeNode* root, int start) 
    {
        map<TreeNode*,TreeNode*> par;
        TreeNode* end = Parent(root,par,start);
        int mx = TimeTaken(end,par);
        return mx;
    }
};
