/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
       TreeNode *tar;
    void findTargetNode(TreeNode *root, int target)
    {
        if (root == nullptr)
        {
            return;
        }

        if (root->val == target)
        {
            tar = root;
            return;
        }

        findTargetNode(root->left, target);
        findTargetNode(root->right, target);
    }

    void makeParents(TreeNode *root, unordered_map<TreeNode *, TreeNode *> &parent)
    {
        queue<TreeNode *> q;
        q.push(root);

        while (!q.empty())
        {
            TreeNode *node = q.front();
            q.pop();

            if (node->left)
            {
                parent[node->left] = node;
                q.push(node->left);
            }
            if (node->right)
            {
                parent[node->right] = node;
                q.push(node->right);
            }
        }
    }

    int amountOfTime(TreeNode *root, int start)
    {
        if (!root)
            return 0;

        unordered_map<TreeNode *, TreeNode *> parent;
        makeParents(root, parent);
        findTargetNode(root, start);

        queue<pair<TreeNode *, int>> q;
        unordered_map<TreeNode *, bool> visited;
        q.push({tar, 0});
        visited[tar] = 1;
        int ans = 0;

        while (!q.empty())
        {
            TreeNode *node = q.front().first;
            int time = q.front().second;
            q.pop();
            ans = max(ans, time);

            // left
            if (node->left and !visited[node->left])
            {
                q.push({node->left, time + 1});
                visited[node->left] = 1;
            }

            // right
            if (node->right and !visited[node->right])
            {
                q.push({node->right, time + 1});
                visited[node->right] = 1;
            }

            // parent
            if (parent.find(node) != parent.end() and !visited[parent[node]])
            {
                q.push({parent[node], time + 1});
                visited[parent[node]] = 1;
            }
        }
        return ans;
    }
};
