class Solution {
    public:
    TreeNode* makemapping(TreeNode* root,map<TreeNode*,TreeNode*> &parent,int start)
{
    TreeNode* tip = NULL;
    queue<TreeNode*>q;
    q.push(root);
    parent[root] = NULL;
    while(!q.empty())
    {
        TreeNode* curr = q.front();
