class Solution {
public: int minute=0;
    int amountOfTime(TreeNode* root, int start) {
        traversal(root,start);
        return minute;   
    }
    int traversal(TreeNode*t,int &start)
    {
        if(t==NULL)
            return -1;
        if(t->val==start)
        {
            helper(t,0);
            return 0;
        }
        int dl=traversal(t->left,start);
        if(dl!=-1)
        {
            helper(t->right,dl+2);
            if(dl+1>minute)
                minute=dl+1;
            return dl+1;

        }
        int dr=traversal(t->right,start);
        if(dr!=-1)
        {
            helper(t->left,dr+2);
            if(dr+1>minute)
                minute=dr+1;
            return dr+1;
        }
        return -1;   
    }
    void helper(TreeNode *t,int level)
    {
        if(t==NULL)
            return;
        if(level>minute)
             minute=level;
        helper(t->left,level+1);
        helper(t->right,level+1);   
    }
};
