class Solution {
//keep a track of the parents in an unordered_map and store it by traversing (BFS) 
void mark_parents(TreeNode* root, unordered_map<TreeNode*, TreeNode*> &markparent)
    {
        queue<TreeNode*> q;
        q.push(root);

        while(!q.empty())
        {
            TreeNode *curr = q.front();
            q.pop();
            if(curr->left!=NULL)
            {
                markparent[curr->left] = curr;
                q.push(curr->left);
            }
            if(curr->right!=NULL)
            {
                markparent[curr->right] = curr;
                q.push(curr->right);
            }
        }
    }
TreeNode* ifNodeExists(TreeNode* node, int start)
{
    if (node == NULL)
        return NULL;

    if (node->val == start)
        return node;

    /* then recur on left subtree */
    TreeNode* res1 = ifNodeExists(node->left, start);
    // node found, no need to look further
    if(res1) return res1;

    /* node is not found in left,
    so recur on right subtree */
    TreeNode* res2 = ifNodeExists(node->right, start);

    return res2;
}
public:
    int amountOfTime(TreeNode* root, int start) {

        unordered_map<TreeNode*,TreeNode*> markparent;
        mark_parents(root,markparent);



        queue<TreeNode*> q;
        unordered_map<TreeNode*, bool> visited;
        TreeNode* target = ifNodeExists(root,start);
        q.push(target);
        visited[target] = true;
        int time = -1;


        while(!q.empty())
        {
            int size = q.size();
            time++;

            for(int i=0;i<size;i++)
            {
                TreeNode *curr = q.front();
                q.pop();

                if(curr->left && !visited[curr->left])
                {
                    q.push(curr->left);
                    visited[curr->left]  = true;
                }

                if(curr->right && !visited[curr->right])
                {
                    q.push(curr->right);
                    visited[curr->right] = true;
                }
                if(markparent[curr] && !visited[markparent[curr]])
                {
                    q.push(markparent[curr]);
                    visited[markparent[curr]] = true;
                }
            }
        }

        return time;
        }

};
