// Easy BFS implementation. Instead of 'root', node with 'start' value becomes the starting node for BFS.
class Solution {
	// Start node for BFS
    TreeNode origin=null;

    public int amountOfTime(TreeNode root, int start) {
        int time=0;
        if(root==null) return time;

        HashMap<TreeNode,TreeNode> map=new HashMap<>();
        getParentMap(root, start, map);

        Queue<TreeNode> q=new LinkedList<>();
        q.add(origin);

        HashSet<TreeNode> visited=new HashSet<>();

        while(q.size()>0){
            int size=q.size();

            while(size-->0){
                TreeNode x=q.poll();
                visited.add(x);

                if(x.left!=null && !visited.contains(x.left)) q.add(x.left);
                if(x.right!=null && !visited.contains(x.right)) q.add(x.right);
                if(map.containsKey(x) && !visited.contains(map.get(x))) q.add(map.get(x));
            }
            time++;
        }
        return time-1;
    }

	// Populates Map[To travel to parent node as well] -> (Child : Parent)
    private void getParentMap(TreeNode root, int start, HashMap<TreeNode,TreeNode> map){
        if(root==null) return;

        if(root.left!=null) map.put(root.left,root);
        if(root.right!=null) map.put(root.right,root);

		// Defining start node for BFS
        if(root.val==start) origin=root;

        getParentMap(root.left,start,map);
        getParentMap(root.right,start,map);
    }
}
```

