class Solution {
public:
    int amountOfTime(TreeNode* root, int start) {

        map<TreeNode *, TreeNode *> m; //keeping parent of current node
        m[root] =0; //initialising root with no parent
        queue<TreeNode *> q;
        q.push(root);
        TreeNode*target;
        while (!q.empty()) //bfs traversal for map
        {
            TreeNode *temp = q.front();
            q.pop();
            if(temp->val==start)target=temp; //finding infected node
            if (temp->left)
            {
                q.push(temp->left);
                m[temp->left] = temp;
            }
            if (temp->right)
            {
                q.push(temp->right);
                m[temp->right] = temp;
            }
        }
        queue<TreeNode *> l; // traversing with bfs approch on all adjacent nodes simaltaneously 
        l.push(target);
        int dist = 0;
        set<int> st; // marking node as visited
        while (!l.empty())
        {
            dist++; // checking time or distance covered
            int sz = l.size();
            while (sz--)
            {
                TreeNode *r = l.front();
                l.pop();

                    if (r->left&&st.find(r->left->val)==st.end()) //if not visited 
                        l.push(r->left);
                    if (r->right&&st.find(r->right->val)==st.end())
                        l.push(r->right);
                    if (m[r]&&st.find(m[r]->val)==st.end())
                        l.push(m[r]);

                st.insert(r->val);
            }
        }
        return dist-1;

    }
};
