class Solution {
    vector<int> dfs(TreeNode* root,int &ans,int &start){
        if(!root) return {0,0};
        if(root->val==start){
            ans=max(ans,max(dfs(root->left,ans,start)[1],dfs(root->right,ans,start)[1] ) );
            return {1,0};
        }

        vector<int> l= dfs(root->left,ans,start);
        vector<int> r= dfs(root->right,ans,start);
