class Solution 
{
    public:
    unordered_map<int,int> childToParent;  
    unordered_map<int,int> leftChild;
    unordered_map<int,int> rightChild;
    unordered_map<int,int> vis;
    void cTP(TreeNode* root)
    {
        if(!root)
            return;
        cTP(root->left);
        if(root->left)
            childToParent[root->left->val]=root->val;
        if(root->right)
            childToParent[root->right->val]=root->val;
        cTP(root->right);
    }
    void pTC(TreeNode* root)
    {
        if(!root)
            return;
        pTC(root->left);
        if(root->left)
            leftChild[root->val]=root->left->val;
        if(root->right)
            rightChild[root->val]=root->right->val;
        pTC(root->right);
    }
    int bfs(int s)
    {
        int ans=0;
        queue<int> q;
        q.push(s);
        while(!q.empty())
        {
            int n=q.size();
            for(int i=0;i<n;i++)
            {
                int temp=q.front();
                q.pop();
                vis[temp]=1;
                if(childToParent[temp]!=-1 && !vis[childToParent[temp]])
                    q.push(childToParent[temp]);
                if(leftChild[temp] && !vis[leftChild[temp]])
                    q.push(leftChild[temp]);
                if(rightChild[temp] && !vis[rightChild[temp]])
                    q.push(rightChild[temp]);
            }
            ans++;
        }
        return ans;
    }
    int amountOfTime(TreeNode* root, int start) 
    {
        childToParent[root->val]=-1;
        cTP(root);
        pTC(root);
        return bfs(start)-1;
    }
};
