/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int amountOfTime(TreeNode* root, int start) {
        vector<int> adj[100006];
        queue<TreeNode*> q;
        q.push(root);
        q.push(NULL);

        while( !q.empty() ) {
            TreeNode* curr = q.front();
            q.pop();
            if(curr) {
                if(curr->left) {
                    q.push(curr->left);
                    adj[curr->val].push_back(curr->left->val);
                    adj[curr->left->val].push_back(curr->val);
                }
                if(curr->right) {
                    q.push(curr->right);
                    adj[curr->val].push_back(curr->right->val);
                    adj[curr->right->val].push_back(curr->val);
                }
            } 
            else if(!q.empty()){
                q.push(NULL);
            }
        }

        int count = 0;
        queue<int> bfs;
        bfs.push(start);
        queue<int> qu;
        vector<int> vis(100006, 0);

        while( !bfs.empty() ) {
            count++;
            while(!bfs.empty()) {
                int curr = bfs.front();
                vis[curr] = 1;
                bfs.pop();
                for(auto it : adj[curr]) {
                    if(!vis[it]) {
                        qu.push(it);
                        vis[it] = 1;
                    }
                }
            }
            swap(qu, bfs);
        }

        return count-1;

    }
};
